import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Sport.scss";
function Sport() {
  const [data, setData] = useState([]);

  const fetchApi = () =>
    axios
      .get(
        "https://newsapi.org/v2/top-headlines?country=in&category=sport&apiKey=f8797d61a4c74a3ea9212b80328ad556"
      )
      .then((res) => setData(res.data.articles));
  useEffect(() => {
    fetchApi();
  }, []);

  return (
    <div className="home">
      <div className="grid2">
        {data
          ? data.map((data, index) => {
              return (
                <div className="headlines2" key={index}>
                  <a href={data.url} target="_blank">
                    <img
                      className={`img${index} headlines2img`}
                      src={
                        data.urlToImage
                          ? data.urlToImage
                          : "data:image/png;base64,"
                      }
                      alt={`img${index}`}
                    />
                  </a>
                  <h3 className="headlines2h3">
                    {data.title}
                    <br />
                    <p>{data.publishedAt}</p>
                  </h3>
                </div>
              );
            })
          : null}
      </div>
    </div>
  );
}

export default Sport;
